import React, {useReducer} from 'react'
import './Products.css';

const currencyOptions = {
    minimumFractionDigits:2,
    maximumFractionDigits:2,
}
function getTotal(cart) {
    const total = cart.reduce((totalCost, item) => totalCost + item);
return total.toLocaleString(undefined, currencyOptions);
}
const products = [
    {
        emoji:'',
        name:'ice cream',
        price: 5
    },
    {
        emoji:'',
        name:'donate',
        price: 2.5
    },
    {
        emoji:'',
        name:'watermelon',
        price: 4
    },
];
function cartReducer(state, action) {
    switch (action.type) {
        case 'add':
            return [...state, action.product];
        case 'remove':
            const productIndex = state.findIndex(item => item.id === action.product);
            if(productIndex < 0) {
            return state;
            }
            const update = [...state];
            update.splice(productIndex, 1);
            
        default:
            return state;

    }
}
function totalReducer(state, action) {
    if (action.type === 'add') {
        return state + action.price;
    }
    return state - action.price;
}
function Products() {
    const [cart, setCart] = useReducer(cartReducer, []);
    const [total, setTotal] = useReducer(totalReducer, 0);
    
    function add(product) {
        const {name, price} = product;
        setCart({name, type: 'add'});
        SetTotal({price, type: 'add'});
    }
    function remove (product) {
        const {name, price} = product;
        setCart({name, type: 'remove'});
        setTotal({price, type: 'remove'});

    }

  return (
    <div className='wrapper'>
        <div>
            Shopping Cart: {cart.length} total items.
        </div>
       <div> Total: {getTotal(total)}</div> 
        <div>
            {products.map(product => 
                (<div className='product'>
                    <span role="img" arial-label={product.name}>{product}</span>
                <button onClick={() => add(product)}>Add</button> 
                <button onClick={() => remove(product)}>Remove</button>               </div>)
            )}
        </div>
       
    </div>
  )
}

export default Products
