import React, {useState} from 'react'
import './Products.css';

const currencyOptions = {
    minimumFractionDigits:2,
    maximumFractionDigits:2,
}
const products = [
    {
        emoji:'',
        name:'ice cream',
        price: 5
    },
    {
        emoji:'',
        name:'donate',
        price: 2.5
    },
    {
        emoji:'',
        name:'watermelon',
        price: 4
    },
];
function Products() {
    const [cart, setCart] = setState([]);
    const [total, setTotal] = setState(0);
    
    function add() {
        setCart('ice cream');
        setTotal(5);
    }

  return (
    <div className='wrapper'>
        <div>
            Shopping Cart: {cart.length} total items.
        </div>
        <div> Total: {getTotal(total)}</div>
        <div className='products'><span role="img" arial-label="ice cream"> Ice Cream</span></div>

<button onClick={add}>Add</button> 
<button
onClick={() =>{
    setCart([]);
    setTotal(0);
}}
>Remove</button>
</div>
  )
}

export default Products
